package Operators;
//Comparision Operator or relational operator
//Compare to two variables...checking true or false....
//==,!=,>,<.>=,<=;
public class ComparisionOprator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double height=161.8;
		double weight=58.8;
		System.out.println(height==weight);//equal to operator
		System.out.println(height!=weight);//not equal operator
		System.out.println(height>weight);//greater operator
		System.out.println(height<weight);//less operator
		System.out.println(height>=weight);//greater then equal operator
		System.out.println(height<=weight);//less then equal to
	}

}
